import random
import json

def generate_random_recipe(user_id, recipe_number):
    nome = f"Receita {recipe_number}"
    foto = "data:image/png;base64,"
    visibilidade = "PUBLICA"
    cores = random.choice([True, False])

    material_categories = {
        "lãs e barbantes": list(range(1, 401)),
        "lãs algodão 4 e 8": list(range(212, 239)) + list(range(266, 293)) + list(range(23, 41)) + list(range(59, 77)),
        "lãs amigurumi pelúcia": list(range(293, 320)) + list(range(374, 401)),
        "lãs amigurumi normal": list(range(320, 374)),
        "acessórios gerais": list(range(401, 473)),
        "botões": list(range(410, 430)),
        "acessórios de rosto": list(range(436, 467)),
        "natal": list(range(467, 473)),
        "agulhas": list(range(473, 501)),
    }

    selected_materials = []

    # Seleciona materiais com base nas proporções
    if random.random() < 0.25:
        # 25% das receitas com agulha 477 e lãs algodão 4 e 8 ou barbante 4 e 8
        selected_materials.append(477)
        selected_materials.extend(random.sample(material_categories["lãs algodão 4 e 8"], random.randint(1, 2)))
    elif random.random() < 0.45:
        # 20% das receitas com acessórios de rosto e lãs amigurumi
        selected_materials.extend(random.sample(material_categories["acessórios de rosto"], random.randint(1, 2)))
        selected_materials.extend(random.sample(material_categories["lãs amigurumi pelúcia"], random.randint(1, 2)))
    elif random.random() < 0.5:
        # 5% das receitas com materiais de Natal
        selected_materials.append(random.choice(material_categories["natal"]))
        selected_materials.extend(random.sample(material_categories["lãs e barbantes"], random.randint(1, 2)))
    else:
        # Resto das receitas com materiais aleatórios
        category = random.choice(list(material_categories.keys()))
        selected_materials.extend(random.sample(material_categories[category], random.randint(1, 2)))

    receitaSeparadaCategoriaIds = [random.randint(1, 50) for _ in range(random.randint(1, 2))]
    
    etapas = []
    num_etapas = random.randint(1, 5)
    for i in range(num_etapas):
        descricao_etapas = f"Etapa {i+1}"
        passosList = [{"descricao": f"Passo {j+1}"} for j in range(random.randint(1, 5))]
        etapa = {"descricao_etapas": descricao_etapas, "passosList": passosList}
        etapas.append(etapa)
    
    recipe = {
        "receita": {
            "nome": nome,
            "foto": foto,
            "nivel_experiencia": "INTERMEDIARIO",  # Nível de experiência intermediário por padrão
            "visibilidade": visibilidade,
            "cores": cores
        },
        "userId": user_id,
        "receitaUtilizaMaterialIds": selected_materials,
        "receitaSeparadaCategoriaIds": receitaSeparadaCategoriaIds,
        "etapas": etapas
    }
    
    return recipe

recipes = []
recipe_counter = 1
for user_id in range(1, 101):
    for _ in range(10):
        recipe = generate_random_recipe(user_id, recipe_counter)
        recipes.append(recipe)
        recipe_counter += 1

with open("receitas.json", "w") as file:
    json.dump(recipes, file, indent=4)

print("Receitas geradas e salvas em 'receitas.json'.")